function buildTable(data){
  var table = document.getElementById('aptsTable');

  table.innerHTML = ''

  // if $('#squishmallows-tab').hasClass('active') {
    for (var i = 0; i < data.length; i++){
		 var row = `<tr class="view" onclick="toggle()">
						   <td>${data[i].aptName}</td>
						   <td>${data[i].twoBedLow}</td>
						   <td>${data[i].utilities}</td>
						   <td>${data[i].distance}</td>
						   <td>${data[i].laundry}</td>
						 </tr>`

		 table.innerHTML += row
    }
  // }

}

//Create the tables
$('th').on('click', function(){

	var column = $(this).data('column')
	var order = $(this).data('order')

	var text = $(this).html()
	text = text.substring(0, text.length - 1)

	if(order == 'desc'){
		$(this).data('order', "asc")
		aptsTable = aptsTable.sort((a,b) => a[column] > b[column] ? 1 : -1)
		text += '&#9660'
	}else{
		$(this).data('order', "desc")
		aptsTable = aptsTable.sort((a,b) => a[column] < b[column] ? 1 : -1)
		text += '&#9650'
	}

	$(this).html(text)
	buildTable(aptsTable)

})


